import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import Layout from '../components/Layout/Layout';
import ProductGrid from '../components/Products/ProductGrid';
import productService from '../services/productService';

const HomePage = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadProducts();
  }, []);

  const loadProducts = async () => {
    try {
      const data = await productService.getAllProducts();
      setProducts(data.slice(0, 8)); // Mostrar solo 8 productos
    } catch (error) {
      console.error('Error cargando productos:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <Layout>
      {/* Hero Section */}
      <section className="bg-gradient-to-r from-gray-900 to-gray-800 text-white py-20">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
          <h2 className="text-5xl md:text-6xl font-bold mb-4">
            Compras <span className="text-purple-400">Inteligentes</span>
          </h2>
          <p className="text-lg text-gray-300 mb-8 max-w-2xl mx-auto">
            Descubre productos recomendados especialmente para ti
          </p>
          <Link to="/register" className="btn-primary">
            Comenzar Ahora
          </Link>
        </div>
      </section>

      {/* Products Section */}
      <section className="max-w-7xl mx-auto px-6 py-16">
        {loading ? (
          <div className="text-center py-12">
            <div className="text-2xl font-bold gradient-text">Cargando productos...</div>
          </div>
        ) : (
          <ProductGrid products={products} title="Productos Destacados" />
        )}
      </section>

      {/* Info Banner */}
      <section className="bg-gradient-to-r from-purple-600 to-blue-500 py-12 my-16">
        <div className="max-w-7xl mx-auto px-6 text-center text-white">
          <h3 className="text-3xl font-bold mb-4">Razonamiento Tech Boutique</h3>
          <p className="max-w-2xl mx-auto">
            Nuestro sistema analiza autom√°ticamente tus preferencias
            para recomendarte solo los productos perfectos para ti.
          </p>
        </div>
      </section>
    </Layout>
  );
};

export default HomePage;
