import axios from 'axios';

const API_URL = 'http://localhost:8081/api/productos';

// Instancia de axios para buscarProductos
const axiosInstance = axios.create({
  baseURL: API_URL,
  headers: {
    'Cache-Control': 'no-cache',
    'Pragma': 'no-cache'
  }
});

// Interceptor para JWT
axiosInstance.interceptors.request.use(
  (config) => {
    const user = JSON.parse(localStorage.getItem('user') || '{}');
    if (user.token) {
      config.headers.Authorization = `${user.type || 'Bearer'} ${user.token}`;
    }
    return config;
  },
  (error) => Promise.reject(error)
);

const searchService = {
  buscarProductos: async (params) => {
    const response = await axiosInstance.get('/buscar', { params });
    return response.data;
  },

  obtenerCategorias: async () => {
    const response = await fetch('http://localhost:8081/api/productos/categorias');
    return response.json();
  },

  obtenerMarcas: async () => {
    const response = await fetch('http://localhost:8081/api/productos/marcas');
    return response.json();
  },

  obtenerRangoPrecios: async () => {
    const response = await fetch('http://localhost:8081/api/productos/rango-precios');
    return response.json();
  },

  obtenerSugerencias: async (query, limite = 5) => {
    const response = await axiosInstance.get('/sugerencias', {
      params: { q: query, limite }
    });
    return response.data;
  },
};

export default searchService;
