import axios from 'axios';

const API_URL = 'http://localhost:8081/api/productos';

// Crear instancia de axios sin cache
const axiosInstance = axios.create({
  baseURL: API_URL,
  headers: {
    'Cache-Control': 'no-cache',
    'Pragma': 'no-cache'
  }
});

const searchService = {
  /**
   * BÃºsqueda avanzada con todos los filtros
   */
  buscarProductos: async (params) => {
    try {
      const response = await axiosInstance.get('/buscar', { params });
      console.log('ðŸ” Respuesta buscar:', response.data);
      return response.data;
    } catch (error) {
      console.error('Error en bÃºsqueda:', error);
      throw error;
    }
  },

  /**
   * Obtener todas las categorÃ­as disponibles
   */
  obtenerCategorias: async () => {
    try {
      console.log('ðŸ“‚ Llamando a /categorias...');
      // Usar fetch() para evitar cache de axios
      const response = await fetch(`${API_URL}/categorias`, {
        method: 'GET',
        headers: {
          'Cache-Control': 'no-cache, no-store, must-revalidate',
          'Pragma': 'no-cache',
          'Expires': '0'
        }
      });
      const data = await response.json();
      console.log('ðŸ“‚ Respuesta categorias:', data);
      return data;
    } catch (error) {
      console.error('Error obteniendo categorÃ­as:', error);
      throw error;
    }
  },

  /**
   * Obtener todas las marcas disponibles
   */
  obtenerMarcas: async () => {
    try {
      console.log('ðŸ·ï¸ Llamando a /marcas...');
      // Usar fetch() para evitar cache de axios
      const response = await fetch(`${API_URL}/marcas`, {
        method: 'GET',
        headers: {
          'Cache-Control': 'no-cache, no-store, must-revalidate',
          'Pragma': 'no-cache',
          'Expires': '0'
        }
      });
      const data = await response.json();
      console.log('ðŸ·ï¸ Respuesta marcas:', data);
      return data;
    } catch (error) {
      console.error('Error obteniendo marcas:', error);
      throw error;
    }
  },

  /**
   * Obtener rango de precios (min y max)
   */
  obtenerRangoPrecios: async () => {
    try {
      const response = await axiosInstance.get('/rango-precios');
      console.log('ðŸ’° Respuesta rango precios:', response.data);
      return response.data;
    } catch (error) {
      console.error('Error obteniendo rango de precios:', error);
      throw error;
    }
  },

  /**
   * Obtener sugerencias de autocompletado
   */
  obtenerSugerencias: async (query, limite = 5) => {
    try {
      const response = await axiosInstance.get('/sugerencias', {
        params: { q: query, limite }
      });
      return response.data;
    } catch (error) {
      console.error('Error obteniendo sugerencias:', error);
      throw error;
    }
  },
};

export default searchService;
