import React from 'react';

const ProductCard = ({ product }) => {
  return (
    <div className="card-hover bg-white rounded-2xl overflow-hidden shadow-md">
      <div className="bg-gradient-to-br from-purple-400 to-purple-600 h-48 flex items-center justify-center">
        <span className="text-6xl">üì¶</span>
      </div>
      <div className="p-4">
        <span className="inline-block bg-purple-100 text-purple-700 text-xs font-semibold px-3 py-1 rounded-full mb-2">
          {product.tipo || 'Producto'}
        </span>
        <h4 className="font-bold text-slate-800 mb-1">{product.nombre}</h4>
        <p className="text-sm text-slate-600 mb-3">{product.marca}</p>
        <div className="flex items-center justify-between">
          <span className="text-2xl font-bold text-purple-600">
            S/.{product.precio}
          </span>
          <div className="flex text-yellow-400">‚≠ê‚≠ê‚≠ê‚≠ê‚≠ê</div>
        </div>
        {product.stock !== undefined && (
          <p className="text-xs text-slate-500 mt-2">Stock: {product.stock}</p>
        )}
      </div>
    </div>
  );
};

export default ProductCard;
